__ectool_advice(){
    echo ""
    echo -en "\033[0;31m$1\033[0m"
    COMPREPLY=('~=~=~=~=~=~' '=~=~=~=~=~=')
}

__ectool_api_usage(){
    echo ""
    echo -en "\033[0;32m$1\033[0m"
}

__ectool(){
    local cur prev i j api_current_desc api_args api_desc

    local api_show_help=1
    local adv_return

    local OLDIFS="$IFS"
    local IFS=$'\n'

    local base_args=(
        '--help'
        '--version'
        '--server <hostname>'
        '--port <port>'
        '--securePort <securePort>'
        '--secure <0|1>'
        '--timeout <s>'
        '--retryTimeout <s>'
        '--retry <0|1>'
        '--user <username>'
        '--setDefault <0|1>'
        '--encoding <charEncoding>'
        '--dryrun'
        '--silent'
        '--suppressAdvisoryMessages <0|1>'
        '--valueOf'
        '--format <xml|json>'
        '--ignoreEnvironment'
    )
    local all_args=("${base_args[@]}" "${!__ectool_api_desc[@]}")

    local param_bool01=(
        0
        1
    )

    COMPREPLY=()

    #_get_comp_words_by_ref cur prev words cword
    cur="${COMP_WORDS[COMP_CWORD]}"
    prev="${COMP_WORDS[COMP_CWORD-1]}"

    local cnt=1
    local api

    while [ $cnt -lt $COMP_CWORD ]; do

        local cword=${COMP_WORDS[$cnt]}

        if [[ -n ${__ectool_api_desc[$cword]} ]]; then
            api=$cword
            break
        fi

        (( cnt++ ))

    done

    if [[ -n $api ]]; then

        # api related
        COMPREPLY=()
        local IFS='~'
        local args=(${__ectool_api_args[$api]})
        local IFS=$'\n'

        if [[ $api = 'import' ]]; then
            args+=('--file <localFileName>')
        fi

        if [[ ${#args[*]} -eq 1 ]]; then
            if [[ $api = 'importLicenseData' ]]; then
                adv_return=file
            else
                COMPREPLY=('~=~=~=~=~=~' '=~=~=~=~=~=')
            fi
        else

            if [[ $prev != '--help' ]]; then
                for (( i=0; i<=$(( ${#args[*]} - 1 )); i++ )); do
                    local param="${args[$i]%% *}"
                    if [[ $param = $prev ]]; then

                        api_show_help=0

                        for (( j=0; j<=$(( ${#__ectool_api_file[*]} - 1 )); j++)); do
                            if [[ "$api $param" = ${__ectool_api_file[$j]} ]]; then
                                adv_return=file
                                break
                            fi
                        done

                        if [[ -n $adv_return ]]; then
                            break
                        fi

                        for (( j=0; j<=$(( ${#__ectool_api_dir[*]} - 1 )); j++)); do
                            if [[ "$api $param" = ${__ectool_api_dir[$j]} ]]; then
                                adv_return=dir
                                break
                            fi
                        done

                        if [[ -n $adv_return ]]; then
                            break
                        fi

                        local param_val="${args[$i]#* }"
                        param_val=${param_val/</}
                        param_val=${param_val/>/}

                        if [[ $param_val == *'|'* ]]; then
                            local IFS='|'
                            args=($param_val)
                            local IFS=$'\n'
                        else
                            __ectool_advice "<enter the \"$param_val\" here>"
                        fi

                        break

                    fi
                done
            fi

            if [[ ${#COMPREPLY[*]} -eq 0 && -z $adv_return ]]; then
                COMPREPLY=($(compgen -W '${args[*]}' -- "$cur"))
            fi

        fi

    else

        case "$prev" in
            --server)
                __ectool_advice '<enter server hostname here>'
                ;;
            --port)
                __ectool_advice '<enter server port here>'
                ;;
            --securePort)
                __ectool_advice '<enter server secure port here>'
                ;;
            --timeout)
                __ectool_advice '<enter timeout in seconds here>'
                ;;
            --retryTimeout)
                __ectool_advice '<enter retry timeout in seconds here>'
                ;;
            --user)
                __ectool_advice '<enter username here>'
                ;;
            --encoding)
                __ectool_advice '<enter encoding here>'
                ;;
            --secure|--retry|--setDefault|--suppressAdvisoryMessages)
                COMPREPLY=($(compgen -W '${param_bool01[*]}' -- "$cur"))
                ;;
            *)
                COMPREPLY=($(compgen -W "${all_args[*]}" -- "$cur"))
                ;;
        esac

    fi

    if [[ ${#COMPREPLY[*]} -eq 1 ]]; then
        COMPREPLY=(${COMPREPLY[0]%% *})
    else
        for (( i=0; i<=$(( ${#COMPREPLY[*]} - 1 )); i++ )); do
            local reply="${COMPREPLY[$i]%% *}"
            if [[ $reply = $cur ]]; then
                COMPREPLY=($reply)
                break
            fi
        done
    fi

    if [[ ${#COMPREPLY[*]} -gt 1 && -n $api && api_show_help -eq 1 ]]; then
        __ectool_api_usage "Usage: ${__ectool_api_desc[$api]}"
    fi

    if [[ $adv_return = 'dir' ]]; then
        compopt -o dirnames
        compopt +o default
        #COMPREPLY=($(compgen -S "/" -d -- ${cur}))
    fi

    IFS="$OLDIFS"
}

complete -o default -F __ectool ectool ectool.exe
